    "URL": "{{ $('Error Trigger').item.json.execution.url }}",
    "Timestamp": "{{ $now.format('yyyy-MM-dd HH:mm:ssa')  }}",
    "Node": "{{  $('Error Trigger').item.json.execution.error.node.name  }}",
    "Error Message":"{{  $('Error Trigger').item.json.execution.error.message }}",
    "Execution ID": "{{  $('Error Trigger').item.json.execution.id }}"
  }
}
