const keywords = ['财经', '金融', '投资', '理财', '经济', '股市', 'A股', '港股', '基金', '银行', '券商', '债券', 'IPO', '货币', '通胀', 'AI', '机器人', '科技'];
const results = [];

for (const item of items) {
  const markdown = item.json.data?.markdown || '';

  // 判断内容是否包含任一关键词
  const isRelevant = keywords.some(keyword =>
    markdown.toLowerCase().includes(keyword.toLowerCase())
  );

  if (isRelevant) {
    // 提取第一个 markdown 链接作为标题和链接
    const match = markdown.match(/\[([^\]]+)\]\((https?:\/\/[^\)]+)\)/);
    const title = match ? match[1] : '';
    const link = match ? match[2] : '';
    const content = markdown;

    const summary = 
      `AI Research Summary:\n` +
      `Title: ${title}\n` +
      `Link: ${link}\n` +
      `Content: ${content}`;

    results.push({ json: { summary } });
  }
}

return results;
